import React, {Component} from 'react'
import {setSessionStorage, getSessionStorage} from '../../utils/storage'
import { Form, Icon, Input, Button, Checkbox } from 'antd';
import './style.less'
const FormItem = Form.Item;
export default class Login extends Component {
    constructor(props) {
        super(props)
    }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
        });
    }

    render() {
        return (
            <section id = {'login'}>
                <Form onSubmit={this.handleSubmit} className="login-form">
                    <FormItem>
                        <h3>管理员登陆</h3>
                    </FormItem>
                    <FormItem>
                        <div className='login_form_item'>
                            <Icon type="user" style={{ color: '#fff', fontSize: 18 }} />
                            <input type="text" className={'login_input'}/>
                        </div>
                    </FormItem>
                    <FormItem>
                        <div className='login_form_item'>
                            <Icon type="lock" style={{ color: '#fff', fontSize: 18 }} />
                            <input type="text" className={'login_input'}/>
                        </div>
                    </FormItem>
                    <FormItem>
                        <Checkbox>Remember me</Checkbox>
                        <div className={'login_form_item'}>
                            <Button type="primary" htmlType="submit" className="login-form-button">
                                登陆
                            </Button>
                            <Button type="primary" htmlType="submit" className="login-form-button login-form-assing">
                                注册
                            </Button>
                        </div>
                        {/*<a className="login-form-forgot" href="">Forgot password</a>*/}
                       {/**/}
                        {/*Or <a href="">register now!</a>*/}
                    </FormItem>
                </Form>
            </section>
        );
    }
    login() {
        setSessionStorage('token', '123123456KKKKJJJ4545gd')
        if(getSessionStorage('token')) this.props.history.push('/')
    }
}
