import React, {Component} from 'react';
import './home.css'
import {Menu, Icon, Layout, Button, Avatar, Tabs} from 'antd'
import RouterChild from "../router/routerChild";
import routerList from '../router/routerList'

class Router extends Component {
    constructor(props) {
        super(props)
        this.state = {
            collapsed: false,
            activeKey: '0',
            count: 1,
            pane: []
        }
    }

    pushPath(v, i) {
        let tabsItem = Object.assign({key: i}, v)
        let flag = !this.state.pane.some(e => e.path === v.path)
        if (flag) {
            this.state.pane.push(tabsItem)
            this.setState({activeKey: JSON.stringify(i), pane: this.state.pane})
        } else {
            this.setState({activeKey: JSON.stringify(i)})
        }
        this.props.history.push(v.path)
    }

    toggleCollapsed = () => {
        this.setState({
            collapsed: !this.state.collapsed
        })
    }

    componentDidMount() {
        const {location} = this.props
        for(let v of this.state.pane) {
            if( v.path === location.pathname) {
                console.log(v)
            }
        }
    }

    render() {
        return (
            <div className={'app'}>
                <Layout>
                    <div className={'nav_container'}>
                        <div>
                            <Button onClick={this.toggleCollapsed}
                                    style={{marginBottom: 10, backgroundColor: '#242f42', border: 'none'}}>
                                <Icon style={{fontSize: 20}} type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}/>
                            </Button>
                            <h1>后台管理系统</h1>
                        </div>

                        <div className={'message_info'}>
                            <Avatar size={64} src="http://blog.gdfengshuo.com/example/work/static/img/img.jpg"/>
                        </div>
                    </div>
                </Layout>
                <section style={{display: 'flex'}}>
                    <div style={!this.state.collapsed ? {width: '200px', height: '100vh'} : {}}>
                        <Menu
                            style={{height: '100vh'}}
                            defaultSelectedKeys={['1']}
                            defaultOpenKeys={['sub1']}
                            mode="inline"
                            theme="dark"
                            inlineCollapsed={this.state.collapsed}
                        >
                            {
                                routerList.menu.map((v, i) =>
                                    <Menu.Item key={v.path}>
                                        <Icon type="inbox"/>
                                        <span className="router_list" style={{color: "#fff"}}
                                              onClick={() => this.pushPath(v, i)}>
                                               {/*<Link to={{pathname: v.path, query: {id: '123456'}}}*/}
                                            {/*style={{color: "#fff"}}>*/}
                                            {v.name}
                                            {/*</Link>*/}
                                           </span>
                                    </Menu.Item>
                                )
                            }

                            {/*<SubMenu key="sub1" title={<span><Icon type="mail" /><span>Navigation One</span></span>}>*/}
                            {/*<Menu.Item key="5">Option 5</Menu.Item>*/}
                            {/*<Menu.Item key="6">Option 6</Menu.Item>*/}
                            {/*<Menu.Item key="7">Option 7</Menu.Item>*/}
                            {/*<Menu.Item key="8">Option 8</Menu.Item>*/}
                            {/*</SubMenu>*/}

                            {/*<SubMenu key="sub2" title={<span><Icon type="appstore" /><span>Navigation Two</span></span>}>*/}
                            {/*<Menu.Item key="9">Option 9</Menu.Item>*/}
                            {/*<Menu.Item key="10">Option 10</Menu.Item>*/}
                            {/*<SubMenu key="sub3" title="Submenu">*/}
                            {/*<Menu.Item key="11">Option 11</Menu.Item>*/}
                            {/*<Menu.Item key="12">Option 12</Menu.Item>*/}
                            {/*</SubMenu>*/}
                            {/*</SubMenu>*/}
                        </Menu>
                    </div>
                    <div className='menu_right'>
                        <Tabs
                            hideAdd
                            onChange={(e) => this.onChange(e)}
                            activeKey={this.state.activeKey}
                            type="editable-card"
                            onEdit={(targetKey, action) => this.onEdit(targetKey, action)}
                        >
                            {
                                this.state.pane.map(pane =>
                                    <Tabs.TabPane tab={pane.name} key={pane.key}>{pane.content}</Tabs.TabPane>
                                )
                            }
                        </Tabs>

                        <RouterChild/>
                    </div>
                </section>
            </div>
        );
    }

    onChange(activeKey) {
        this.setState({activeKey})
        this.pushPath( this.state.pane[activeKey-0], activeKey-0)
    }

    onEdit(targetKey, action) {
        this.setState({
            pane: this.state.pane.splice(targetKey-0, 1)
        })
        console.log(targetKey)
    }
}

export default Router;
