import React, {Component} from 'react';
import './home.css'
import {Menu, Icon, Layout, Button, Avatar, Tabs} from 'antd'
import RouterChild from "../router/routerChild";
import routerList from '../router/routerList'
const SubMenu = Menu.SubMenu
class Router extends Component {
    constructor(props) {
        super(props)
        this.state = {
            collapsed: false,
            activeKey: '0',
            pane: []
        }
    }

    setPane(tabsItem, i) {
        this.state.pane.push(tabsItem)
        this.setState({activeKey: JSON.stringify(i), pane: this.state.pane})
    }

    pushPath(v, i) {
        const {location} = this.props
        if(v.path === location.pathname) return
        let tabsItem = Object.assign({key: String(i)}, v)
        let flag = !this.state.pane.some(e => e.path === v.path)
        if (flag) {
            this.setPane(tabsItem, i)
        } else {
            this.setState({activeKey: String(i)})
        }
        this.props.history.push(v.path)
    }

    toggleCollapsed = () => {
        this.setState({
            collapsed: !this.state.collapsed
        })
    }

    componentDidMount() {
        const {location} = this.props
        routerList.menu.forEach((v, i) => {
            if (v.path === location.pathname) {
                let tabsItem = Object.assign({key: String(i)}, v)
                this.setPane(tabsItem, i)
                return
            }
        })

    }

    render() {
        return (
            <div className={'app'}>
                <Layout>
                    <div className={'nav_container'}>
                        <div>
                            <Button onClick={this.toggleCollapsed}
                                    style={{marginBottom: 10, backgroundColor: '#242f42', border: 'none'}}>
                                <Icon style={{fontSize: 20}} type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}/>
                            </Button>
                            <h1>后台管理系统</h1>
                        </div>

                        <div className={'message_info'}>
                            <Avatar size={64} src="http://blog.gdfengshuo.com/example/work/static/img/img.jpg"/>
                        </div>
                    </div>
                </Layout>
                <section style={{display: 'flex'}}>
                    <div style={!this.state.collapsed ? {width: '200px', height: '100vh'} : {}}>
                        <Menu
                            style={{height: '100vh'}}
                            defaultSelectedKeys={['1']}
                            defaultOpenKeys={['sub1']}
                            mode="inline"
                            theme="dark"
                            inlineCollapsed={this.state.collapsed}
                        >
                            {
                                routerList.menu.map((v, i) => v.children ?
                                    <SubMenu key={v.path} title={<span><Icon type="inbox" /><span className="router_list"  style={{color: "#fff"}}>{v.name}</span></span>}>
                                        {
                                            v.children.map((e, index) =>
                                                    <Menu.Item key={e.path}>
                                                        <Icon type="inbox"/>
                                                        <span className="router_list" style={{color: "#fff"}}
                                                              onClick={() => this.pushPath(e, i)}>
                                                            {e.name}
                                                        </span>
                                                    </Menu.Item>
                                            )
                                        }

                                    </SubMenu> : <Menu.Item key={v.path}>
                                        <Icon type="inbox"/>
                                        <span className="router_list" style={{color: "#fff"}}
                                              onClick={() => this.pushPath(v, i)}>
                                            {v.name}
                                        </span>
                                    </Menu.Item>
                                )
                            }
                        </Menu>
                    </div>
                    <div className='menu_right'>
                        <Tabs
                            hideAdd
                            onChange={(e) => this.onChange(e)}
                            activeKey={this.state.activeKey}
                            type="editable-card"
                            onEdit={(targetKey, action) => this.onEdit(targetKey, action)}
                        >
                            {
                                this.state.pane.map(pane =>
                                    <Tabs.TabPane tab={pane.name} key={pane.key}>{pane.content}</Tabs.TabPane>
                                )
                            }
                        </Tabs>
                        <RouterChild/>
                    </div>
                </section>
            </div>
        );
    }

    onChange(activeKey) {
        this.setState({activeKey})
        const pane = this.state.pane.filter(v => activeKey === v.key)
        this.pushPath(pane[0], activeKey - 0)
    }

    onEdit(targetKey) {
        let activeKey = this.state.activeKey
        let lastIndex = 0
        this.state.pane.forEach((pane, i) => {
            if (pane.key === targetKey) {
                lastIndex = i - 1
                if(!i && this.state.pane.length) {
                    this.onChange(this.state.pane[i+1].key)
                }
            }
        })
        const pane = this.state.pane.filter(pane => pane.key !== targetKey)
        if(lastIndex >= 0 && activeKey === targetKey && pane.length) {
            activeKey = pane[lastIndex].key
            this.onChange(activeKey)
        }
        this.setState({pane, activeKey: activeKey})
    }
}

export default Router;
