import React, { Component } from 'react';
import './home.css'
import { Menu, Icon, Layout, Button, Avatar } from 'antd'
import RouterChild from "../router/routerChild";
import {Link} from 'react-router-dom'
// const SubMenu = Menu.SubMenu

import routerList from '../router/routerList'
class Router extends Component {
    constructor(props) {
        super(props)
        this.state = {
            collapsed: false,
        }
    }
    toggleCollapsed = () => {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    }
    render() {
        return (
            <div className={'app'}>
                <Layout>
                    <div className={'nav_container'}>
                        <div>
                            <Button type="primary" className={'menu_button'} onClick={this.toggleCollapsed} style={{ marginBottom: 16,  backgroundColor: '#242f42' }}>
                                <Icon type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'} />
                            </Button>
                            <h1>后台管理系统</h1>
                        </div>

                        <div className={'message_info'}>
                            <Avatar size={64} src="http://blog.gdfengshuo.com/example/work/static/img/img.jpg" />
                        </div>
                    </div>
                </Layout>
                <section  style={{display: 'flex'}}>
                   <div style={{width: '200px', height: '100vh'}}>
                           <Menu
                            style={{height: '100vh'}}
                            defaultSelectedKeys={['1']}
                            defaultOpenKeys={['sub1']}
                            mode="inline"
                            theme="dark"
                            inlineCollapsed={this.state.collapsed}
                            >
                               {
                                   routerList.menu.map( v=>
                                       <Menu.Item key={v.path}>
                                           <Icon type="inbox"/>
                                           <span className="router_list"> <Link to={v.path} style={{color: "#fff",}}>{v.name}</Link></span>
                                       </Menu.Item>
                                   )
                               }

                            {/*<SubMenu key="sub1" title={<span><Icon type="mail" /><span>Navigation One</span></span>}>*/}
                                {/*<Menu.Item key="5">Option 5</Menu.Item>*/}
                                {/*<Menu.Item key="6">Option 6</Menu.Item>*/}
                                {/*<Menu.Item key="7">Option 7</Menu.Item>*/}
                                {/*<Menu.Item key="8">Option 8</Menu.Item>*/}
                            {/*</SubMenu>*/}

                            {/*<SubMenu key="sub2" title={<span><Icon type="appstore" /><span>Navigation Two</span></span>}>*/}
                                {/*<Menu.Item key="9">Option 9</Menu.Item>*/}
                                {/*<Menu.Item key="10">Option 10</Menu.Item>*/}
                                {/*<SubMenu key="sub3" title="Submenu">*/}
                                    {/*<Menu.Item key="11">Option 11</Menu.Item>*/}
                                    {/*<Menu.Item key="12">Option 12</Menu.Item>*/}
                                {/*</SubMenu>*/}
                            {/*</SubMenu>*/}
                        </Menu>
                    </div>
                    <div className='menu_right'>
                        <RouterChild/>
                    </div>
                </section>
            </div>
        );
    }
}
export default Router;
